import React, {useRef} from 'react';

import  './dropdown.css'; 

const clickOutsideRef = (content_ref, toggle_ref) => {
    document.addEventListener('mousedown', (e) => {
        // user click toggle
        if (toogle_ref.current && toggle_ref.current.contains(e.target)) {
            content_ref.current.classList.toggle('active')
        } else {
            // user click outside toggle and content
            if (content_ref.current && !content_ref.current.contains(e.target)) {
                content_ref.current.classList.remove('active')
            }
        }
    })
}

const Dropdown = props => {

    const dropdown__toggle_ei = useRef(null)
    const dropdown__content_ei = useRef(null)


     clickOutsideRef(dropdown__content_ei, dropdown__toggle_el) 
    return (   
        <div className='dropdown'>
           <button ref={dropdown__toggle_ei} className='dropdown__toggle'>
                {
                    props.icon ? <i className={props.icon}></i> : ''
                }
                    {
                        props.badge ?  <span className="dropdown__toogle-badge">{props.badge}</span> : ''
                    }
                {
                    props.customToggle ? props.customToggle() : ''   
                }
           </button>
            <div ref={dropdown__content_el} className="dropdown__content">
                   
                    {
                        props.contentData && props.renderItems ? props.contentData.map((item, index)=>
                         props.renderItems(item, index)) : ''
                    }

                    {
                        props.renderFooter ? (
                            <div className="dropdown__footer">
                                {props.renderFooter()}
                            </div>
                        ) : ''
                    }
            </div>
        </div>
    )
}

export default Dropdown
