import React, { useState } from "react";
import "./map.css";
import Popup from "./popup";
import { uzbDatas } from "../uzbDatas";
import { useHistory } from "react-router-dom";
import { useSelector } from 'react-redux';


function Map() {
  const [isOpen, setIsOpen] = useState(false);
  const [id, setId] = useState(null);
  const [name, setName] = useState(null);
  const history = useHistory();
  // const allInfos = useSelector(state => state.regionsSlice.allInfos)
  // console.log(allInfos)
  const togglePopup = (id, name) => {
    setId(id);
    setName(name);
    setIsOpen(true);
  };
  const doubleClick = (id) => {
    history.push(`/regionData/${id}`);
  };
  return (
    <div className="regions">
      {isOpen && (
        <Popup
          content={
            <>
              <p
                style={{
                  fontSize: "15px",
                  color: "orange",
                  fontWeight: "bold",
                }}
              >
                id:{id},
                <br />
                name:{name}
              </p>
            

            </>
          }
          handleClose={togglePopup}
        />
      )}
      <svg
        
        id="uzbekistan"
        baseprofile="tiny"
        height="652"
        width="1000"
        stroke="#ffffff"
        stroke-linecap="round"
        stroke-linejoin="round"
        stroke-width="2"
        version="1.2"
        viewbox="0 0 500 500"
        
        onMouseOut={() => setIsOpen(true)}
      >
            <rect
      x="20%"
      y="20%"
      width="1000"
      height="1000"
      rx="20"
      style={{ fill: '#ff0000', stroke: '#000000', strokeWidth: '2px' }}
    />

    <rect
      x="30%"
      y="30%"
      width="1000"
      height="1000"
      rx="40"
      style={{ fill: '#0000ff', stroke: '#000000', strokeWidth: '2px', fillOpacity: 0.7 }}
    />
        {uzbDatas.map((item) => (
           <g>
          <svg

            onDoubleClick={(id) => {
              doubleClick(item.id);
            }}
            onClick={(id, name) => {
              togglePopup(item.id, item.name);
            }}
          >
           
            <path name={item.name} id={item.id} d={item.d} />
          </svg>
          </g>
        ))}
        <g>
        <circle cx="673.4" cy="626" id="0"></circle>
        <circle cx="637.6" cy="506" id="1"></circle>
        <circle cx="636" cy="498.9" id="2"></circle>
      </g>
      </svg>

    </div>
  );
}

export default Map;
